import React, { useState } from 'react';
import axios from 'axios';
import { useHistory, Redirect } from 'react-router-dom';

const Login: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  // Add a local state to track successful login
  const [loggedIn, setLoggedIn] = useState(false);
  const history = useHistory();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Submitting login with:', username, password);
    try {
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}/auth/login`,
        { username, password }
      );
      console.log('Login response:', response.data);
      // Save the token
      localStorage.setItem('token', response.data.token);
      // Set local auth state to true so that the component re-renders and triggers redirection
      setLoggedIn(true);
    } catch (err: any) {
      console.error('Login error:', err.response ? err.response.data : err);
      alert('Invalid credentials');
    }
  };

  // If the user is logged in or a token exists in localStorage, redirect to /tasks
  if (loggedIn || localStorage.getItem('token')) {
    return <Redirect to="/tasks" />;
  }

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username: </label>
          <input 
            value={username} 
            onChange={(e) => setUsername(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Password: </label>
          <input 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            type="password" 
            required 
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
